
public class comparingtwoobjects {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Double x=new Double (123.44);
		Long y=new Long (123);
		System.out.println("checking two objects are equal");
		if(x.equals(y)==true) {
			System.out.println("\n Both are equal");
			
		}
		else {
			System.out.println("\n Both are not equal");
			
		}

	}

}
